/* Function: mdlOutputs =======================================================
 *
*/
static void mdlOutputs(SimStruct *S, int_T tid)
{
    char *Acceleration = (char *) ssGetOutputPortSignal(S,0);
    char *Compass = (char *) ssGetOutputPortSignal(S,1);
    real_T        *Heading  = (real_T *)ssGetOutputPortRealSignal(S,2);
    const real_T   *xD = ssGetDiscStates(S);
    const int_T   p_width0  = mxGetNumberOfElements(PARAM_DEF0(S));
    const int_T   p_width1  = mxGetNumberOfElements(PARAM_DEF1(S));
    const int_T   p_width2  = mxGetNumberOfElements(PARAM_DEF2(S));
    const int_T   p_width3  = mxGetNumberOfElements(PARAM_DEF3(S));
    const int_T   p_width4  = mxGetNumberOfElements(PARAM_DEF4(S));
    const int_T   p_width5  = mxGetNumberOfElements(PARAM_DEF5(S));
    const int_T   p_width6  = mxGetNumberOfElements(PARAM_DEF6(S));
    const int_T   p_width7  = mxGetNumberOfElements(PARAM_DEF7(S));
    const real_T  *sampletime  = (const real_T *)mxGetData(PARAM_DEF0(S));
    const boolean_T  *debug  = (const boolean_T *)mxGetData(PARAM_DEF1(S));
    const real_T  *MinCompassX  = (const real_T *)mxGetData(PARAM_DEF2(S));
    const real_T  *MaxCompassX  = (const real_T *)mxGetData(PARAM_DEF3(S));
    const real_T  *MinCompassY  = (const real_T *)mxGetData(PARAM_DEF4(S));
    const real_T  *MaxCompassY  = (const real_T *)mxGetData(PARAM_DEF5(S));
    const real_T  *MinCompassZ  = (const real_T *)mxGetData(PARAM_DEF6(S));
    const boolean_T  *MaxCompassZ  = (const boolean_T *)mxGetData(PARAM_DEF7(S));


	int_T* busInfo = (int_T *) ssGetUserData(S);

	/* Temporary bus copy declarations */
	accel _y0BUS;
	compass _y1BUS;

	/*Copy input bus into temporary structure*/
	
	LSM303_Outputs_wrapper(&_y0BUS, &_y1BUS, Heading, xD, sampletime, p_width0, debug, p_width1, MinCompassX, p_width2, MaxCompassX, p_width3, MinCompassY, p_width4, MaxCompassY, p_width5, MinCompassZ, p_width6, MaxCompassZ, p_width7);

	/*Copy temporary structure into output bus*/
	(void) memcpy(Acceleration+ busInfo[0], &_y0BUS.x, busInfo[1]);
	(void) memcpy(Acceleration+ busInfo[2], &_y0BUS.y, busInfo[3]);
	(void) memcpy(Acceleration+ busInfo[4], &_y0BUS.z, busInfo[5]);
	(void) memcpy(Compass+ busInfo[6], &_y1BUS.x, busInfo[7]);
	(void) memcpy(Compass+ busInfo[8], &_y1BUS.y, busInfo[9]);
	(void) memcpy(Compass+ busInfo[10], &_y1BUS.z, busInfo[11]);


}
